import React, { Component } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';

 const productItems = [
  {
    id: 1,
    altText: 'Oasis Automation',
    caption: <div className="item-content container">
              <div className="row">
                <div className="col-md-5">

                  <img src="images/device.png" />
                </div>
                <div className="col-md-7">
                  <h3>Event Manager Tool</h3>
                  <h4>Reduce Noise with The Oasis Event Manager</h4>
                </div>
              </div>
             </div>
  },
  // {
  //   id: 2,
  //   altText: 'Oasis Discovery',
  //   caption: 'Slide 2'
  // },
  // {
  //   id: 3,
  //   altText: 'App & Infrastrucutre Monitoring',
  //   caption: 'Slide 3'
  // },
  // {
  //   id: 4,
  //   altText: 'Oasis Analytics',
  //   caption: 'Slide 3'
  // },
  // {
  //   id: 5,
  //   altText: 'Oasis Event Manager',
  //   caption: 'Slide 3'
  // },
  // {
  //   id: 6,
  //   altText: 'Oasis Trap Adaptor',
  //   caption: 'Slide 3'
  // },
  // {
  //   id: 7,
  //   altText: 'Oasis API suite',
  //   caption: 'Slide 3'
  // },
  // {
  //   id: 8,
  //   altText: 'Cybervision',
  //   caption: 'Slide 3'
  // }
];
 const platformItems = [
  {
    id: 1,
    altText: 'Oasis Automation',
    caption: <div>
              <h3>Oasis Automation</h3>
             </div>
  },
  {
    id: 2,
    altText: 'Oasis Discovery',
    caption: 'Slide 2'
  },
  {
    id: 3,
    altText: 'App & Infrastrucutre Monitoring',
    caption: 'Slide 3'
  }
];

class Product extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      items: props.contentName=='product'?productItems:platformItems
    };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
  }
  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === this.state.items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? this.state.items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  render() {
    const { activeIndex } = this.state;

    const slides = this.state.items.map((item) => {
      return (
        <CarouselItem
          className="custom-tag"
          tag="div"
          key={item.id}
          onExiting={this.onExiting}
          onExited={this.onExited}
        >
          {/* <CarouselCaption className="text-danger" captionText={item.caption} captionHeader={''} /> */}
          {item.caption}
        </CarouselItem>
      );
    });

    return (
      <div>
        <style>
          {
            `.custom-tag {
                max-width: 100%;
                height: 500px;
                background: black;
              }`
          }
        </style>
        <ul>
        </ul>
        <Carousel
          activeIndex={activeIndex}
          next={this.next}
          previous={this.previous}
        >
          {/* <CarouselIndicators items={this.state.items} activeIndex={activeIndex} onClickHandler={this.goToIndex} /> */}
          {slides}
          <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
          <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
        </Carousel>
      </div>
    );
  }
}
export default Product;
